    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
         boolean validado = true;
         String senha = campoSenhaCad.getText();
         String senha2 = campoConfirmaSenha.getText();
                 

                
              
        if(campoNome.getText().equals("")){
            JOptionPane.showMessageDialog(null,"Por favor, digite seu nome","AVISO",JOptionPane.WARNING_MESSAGE);
            return;
        }
        
         if(campoCPF.getText().equals("")){
            JOptionPane.showMessageDialog(null,"Por favor, digite seu CPF","AVISO",JOptionPane.WARNING_MESSAGE);
            return;
        }
          if(campoNome.getText().equals("")){
            JOptionPane.showMessageDialog(null,"Por favor, digite seu nome","AVISO",JOptionPane.WARNING_MESSAGE);
            return;
        }
           if (!campoEmailCad.getText().contains("@")){
            JOptionPane.showMessageDialog(null,"Por favor, digite um email válido","AVISO",JOptionPane.WARNING_MESSAGE);
             return;     
        }
        
          if(campoSenhaCad.getText().length()<6){
            JOptionPane.showMessageDialog(null,"Por favor, digite uma senha que tenha no mínimo 6 caracteres ","AVISO",JOptionPane.WARNING_MESSAGE);
            return;
        }
         
          if(senha.equals(senha2)){
              
          }
          else{
             JOptionPane.showMessageDialog(null,"As senhas devem ser iguais ","AVISO",JOptionPane.WARNING_MESSAGE);
             return;
          }


          

        
        Usuarios usuario = new Usuarios();
        CadastroBD CadastroBD = new CadastroBD();

        nome = campoNome.getText();
        cpf = campoCPF.getText();
        telefone = campoTel.getText();
        email = campoEmailCad.getText();
        cidade = campoCidade.getText();
        rua = campoRua.getText();
        senha = campoSenhaCad.getText();
        cep = campoCEP.getText();
        n = campoN.getText();
        uf = campoUF.getText();
        bairro = campoBairro.getText();

        usuario.setNome(nome);
        usuario.setCpf(cpf);
        usuario.setTelefone(telefone);
        usuario.setEmail(email);
        usuario.setCidade(cidade);
        usuario.setRua(rua);
        usuario.setSenha(senha);
        usuario.setCep(cep);
        usuario.setN(n);
        usuario.setUf(uf);
        usuario.setBairro(bairro);
       

        if(CadastroBD.inserirUsuarios(usuario) == true)
        {
            JOptionPane.showMessageDialog(null, "Você foi cadastrado com sucesso!!!", "Cadastro",JOptionPane.INFORMATION_MESSAGE);
            hm = new Login();
            hm.setVisible(true);
            this.dispose();
        }
        else
        {
            JOptionPane.showMessageDialog(null, "Erro no cadastro", "Erro",JOptionPane.ERROR_MESSAGE);
        }
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

